function toggleElements(e){document.querySelectorAll(`${e}, .background-blur`).forEach((e=>{e.classList.contains("hidden")?(e.classList.remove("hidden"),e.classList.add("visible")):(e.classList.remove("visible"),e.classList.add("hidden"))}))}function toggleNav(e){toggleElements(e);const s=document.querySelector(".navigation__menu-button"),t=document.querySelector(".logo");s.classList.toggle("active"),t.classList.toggle("menu-opened")}function toggleClassBasedOnWidth(){const e=document.querySelector(".navigation__links");window.innerWidth<=860?e.classList.add("hidden"):e.classList.remove("hidden")}window.addEventListener("load",toggleClassBasedOnWidth),window.addEventListener("resize",toggleClassBasedOnWidth);